package com.example.recyclerviewevening

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.example.recyclerviewevening.Model.ImageFromNet
import com.example.recyclerviewevening.adapter.ImageFromNetAdapter

class MainActivity : AppCompatActivity() {
    private lateinit var recyclerView: RecyclerView

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)

        recyclerView = findViewById(R.id.recView)

        recyclerView.layoutManager = LinearLayoutManager(this)

        val imgFromNet = ArrayList<ImageFromNet>()
        imgFromNet.add(ImageFromNet("data:image/jpeg;base64,","Mclaren P1","TT V8 +E Motor 903HP"))
        imgFromNet.add(ImageFromNet("data:image/jpeg;base64,","Bugatti Chiron","8L QT W16 1500HP"))
        imgFromNet.add(ImageFromNet("data:image/jpeg;base64,","Ferrari 488 Pista","3.9L V8 711HP"))
        imgFromNet.add(ImageFromNet("data:image/jpeg;base64,","Lamborghini Aventador SVJ","6.5L V12 770HP"))

        val adapter = ImageFromNetAdapter(this,imgFromNet)

        recyclerView.adapter = adapter

    }
}